<?php

namespace SE\InputBundle\Entity;

use Doctrine\ORM\EntityRepository;

/**
 * DepartementRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class DepartementRepository extends EntityRepository
{
	public function getCurrentDepartements()
	{
		$qb = $this
		->createQueryBuilder('a')
		->select("a")
		->where("a.statusControl = 1")
		->getQuery()
		->getResult()
		;
		return $qb;
	}

	public function getHistoricalDepartements($year, $month)
	{
		$start = new \DateTime();
		$end = new \DateTime();
		$start->setDate($year, $month, 1);
		$end = $start->format( 'Y-m-t' );
		$start->modify( '-1 day' );

		$qb = $this
		->createQueryBuilder('a')
		->select("a")
		->where("( a.endDate <> null and a.endDate <= '".$end."' ) or ( a.endDate = null and a.statusControl = 1 ) ")
        ->andWhere("a.startDate >= '".$start->format("Y-m-d")."'")
	    ->orderBy('a.id', 'ASC')
		->getQuery()
		->getResult()
		;
		return $qb;
	}
}
